import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBox.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import { fetchPerson } from "../../store/features/personSlice";
// import greenSparkLogo from "../../assets/images/greenspark-logo.svg";

const WidgetBox: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  const [color, setColor] = useState<string>("#2E3A8C");
  const [color2, setColor2] = useState<string>("#3B755F");
  const [color3, setColor3] = useState<string>("");

  const updateblue = () => {
    setColor("#2e3a8c");
  };

  return (
    <div className="widgetBox">
      <h1>Per Product Widgets</h1>
      <hr />
      <div className="innerBoxes">
        {persons.map((person) => {
          return (
            <div className="outerBox">
              <div
                style={{
                  background:
                    person?.id === 1
                      ? color
                      : person?.id === 2
                      ? color2
                      : color3,
                }}
                className="top"
              >
                {/* <img className="bgImage2" src={greenSparkLogo} alt="bg-image" /> */}
                <p>
                  This product collects
                  <br />
                  {person.amount} {person.type}
                </p>
              </div>
              <div className="bottom">
                <div>
                  <p>Link to public profile</p>
                  <input type="checkbox" />
                </div>
                <div>
                  <p>Badge Color</p>
                  <button onClick={updateblue}>blue</button>
                  <button onClick={updateblue}>green</button>
                  <button onClick={updateblue}>creme</button>
                </div>
                <div>
                  <p>Activate Box</p>
                  <input type="checkbox" />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBox;
