import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBoxs.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import {
  updateWidget,
  fetchPerson,
  activateBadge,
} from "../../store/features/personSlice";
import greenSparkLogo from "../../assets/images/greenspark-logo.svg";
import greenSparkLogoGreen from "../../assets/images/greenspark-logo-green.svg";
import toolTipImg from "../../assets/images/Tooltip.svg";

const WidgetBoxs: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  const updateblue = () => {
    dispatch(updateWidget("#2E3A8C"));
  };

  const updategreen = () => {
    dispatch(updateWidget("#3B755F"));
  };

  const updatecreme = () => {
    dispatch(updateWidget("#F2EBDB"));
  };

  const updatewhite = () => {
    dispatch(updateWidget("white"));
  };

  const updateblack = () => {
    dispatch(updateWidget("black"));
  };

  return (
    <div className="widgetBox">
      <h3>Per Product Widgets</h3>
      <hr />
      <div className="innerBoxes">
        {persons.map((person) => {
          return (
            <div className="outerBox">
              <div
                style={{
                  background: person.selectedColor,
                  color:
                    person.selectedColor === "#F2EBDB"
                      ? "#3B755F"
                      : person.selectedColor === "white"
                      ? "black"
                      : "white",
                }}
                className="top"
              >
                <img
                  src={
                    person.selectedColor === "#F2EBDB"
                      ? greenSparkLogoGreen
                      : greenSparkLogo
                  }
                  alt="greensparkLogo"
                />
                <p>
                  This product {person.action}
                  <br />
                  <span style={{ display: "flex", alignItems: "baseline" }}>
                    {person.amount}
                    {person.type === "carbon" ? <p> kgs of </p> : null}
                    {person.type}
                  </span>
                </p>
              </div>
              <div className="bottom">
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <div className="tooltip">
                    <span className="tooltiptext">Tooltip text</span>
                    {/* <img src={toolTipImg} alt="toolTipImg" /> */}
                  </div>
                  <div className="publicProfile">
                    <p>Link to Public Profile</p>
                    {/* <img src={toolTipImg} alt="toolTipImg" /> */}
                  </div>
                  <div className="navi-dots">
                    <input type="checkbox" />
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                  className="coloredBtns"
                >
                  <p>Badge color</p>
                  <div className="colorBox">
                    <button
                      style={{
                        backgroundColor: "#2E3A8C",
                      }}
                      onClick={updateblue}
                    ></button>
                  </div>

                  <div className="colorBox">
                    <button
                      style={{
                        backgroundColor: "#3B755F",
                        height: 16,
                        width: 16,
                      }}
                      onClick={updategreen}
                    ></button>
                  </div>
                  <div className="colorBox">
                    <button
                      style={{
                        backgroundColor: "#F2EBDB",
                        height: 16,
                        width: 16,
                      }}
                      onClick={updatecreme}
                    ></button>
                  </div>
                  <div className="colorBox">
                    <button
                      style={{
                        backgroundColor: "white",
                        height: 16,
                        width: 16,
                      }}
                      onClick={updatewhite}
                    ></button>
                  </div>
                  <div className="colorBox">
                    <button
                      style={{
                        backgroundColor: "black",
                        height: 16,
                        width: 16,
                      }}
                      onClick={updateblack}
                    ></button>
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <p>Activate badge</p>
                  <p>{JSON.stringify(person.active)}</p>
                  <label
                    className="switch"
                    onClick={() => dispatch(activateBadge(!person.active))}
                  >
                    <input type="checkbox" />
                    <span className="slider round"></span>
                  </label>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBoxs;
