import React from "react";
import { BtnProps } from "../../../types/globalTypes";

const WidgetBtn: React.FC<BtnProps> = ({
  widgetData,
  boxColor,
  boxID,
  updateColor,
}) => {
  return (
    <div>
      <button
        // style={{
        //   backgroundColor: boxColor,
        //   cursor: "pointer",
        // }}
        style={
          widgetData.setFocus
            ? {
                backgroundColor: boxColor,
                cursor: "pointer",
                border: "2px solid pink",
              }
            : {
                backgroundColor: boxColor,
                cursor: "pointer",
                border: "transparent",
              }
        }
        onClick={() => updateColor(widgetData.id, boxColor)}
      ></button>
    </div>
  );
};

export default WidgetBtn;
