import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBoxs.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import {
  updateWidget,
  fetchPerson,
  activateBadge,
  activateLinked,
} from "../../store/features/personSlice";
import greenSparkLogo from "../../assets/images/greenspark-logo.svg";
import greenSparkLogoGreen from "../../assets/images/greenspark-logo-green.svg";
import greenSparkLogoBlack from "../../assets/images/greenspark-logo-black.svg";
import toolTipImg from "../../assets/images/Tooltip.svg";
import WidgetBtn from "../UI/WidgetBtn";

const WidgetBoxs: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  const globalColors = {
    blue: "#2E3A8C",
    green: "#3B755F",
    creme: "",
  };

  const updateColor = (index: number, boxColor: string) => {
    dispatch(updateWidget({ id: index, selectedColor: boxColor }));
  };

  return (
    <div className="widgetBox">
      <h3>Per Product Widgets</h3>
      <hr />
      <div className="innerBoxes">
        {persons.map((widget, i) => {
          return (
            <div key={i} className="outerBox">
              <div
                style={{
                  background: widget.selectedColor,
                  color:
                    widget.selectedColor === globalColors.creme
                      ? globalColors.green
                      : widget.selectedColor === "white"
                      ? "black"
                      : "white",
                }}
                className="top"
              >
                <img
                  src={
                    widget.selectedColor === globalColors.creme
                      ? greenSparkLogoGreen
                      : widget.selectedColor === "white"
                      ? greenSparkLogoBlack
                      : greenSparkLogo
                  }
                  alt="greensparkLogo"
                />
                <p>
                  This product {widget.action}
                  <br />
                  <span>
                    {widget.amount}
                    {widget.type === "carbon" ? <p> kgs of </p> : null}{" "}
                    {widget.type}
                  </span>
                </p>
              </div>
              <div className="bottom">
                <div className="innerBottom">
                  <div className="publicProfile">
                    <p>Link to Public Profile</p>
                    <div className="tooltip">
                      <img src={toolTipImg} alt="toolTipImg" />
                      <span className="tooltiptext">
                        This widget links directly to your public profile so
                        that you can easily share your impact with your
                        customers. Turn it off here if you do not want the badge
                        to link to it.
                        <a
                          aria-disabled
                          href="https://brett-dickenson-portfolio.netlify.app/"
                        >
                          View Public Profile
                        </a>
                      </span>
                    </div>
                  </div>
                  <div className={widget.active ? "navi-dots" : "navi-dots-no"}>
                    <input
                      onClick={() =>
                        dispatch(
                          activateLinked({
                            id: widget.id,
                          })
                        )
                      }
                      disabled={widget.active ? false : true}
                      checked={widget.linked && widget.active ? true : false}
                      type="checkbox"
                    />
                  </div>
                </div>
                <div className="coloredBtns">
                  <p>Badge color</p>
                  <div className="colorBox">
                    <WidgetBtn
                      updateColor={updateColor}
                      widgetData={widget}
                      boxColor={globalColors.blue}
                    />
                    <WidgetBtn
                      updateColor={updateColor}
                      widgetData={widget}
                      boxColor={globalColors.green}
                    />
                    <WidgetBtn
                      updateColor={updateColor}
                      widgetData={widget}
                      boxColor={globalColors.creme}
                    />
                    <WidgetBtn
                      updateColor={updateColor}
                      widgetData={widget}
                      boxColor={"white"}
                    />
                    <WidgetBtn
                      updateColor={updateColor}
                      widgetData={widget}
                      boxColor={"black"}
                    />
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <p>Activate badge</p>
                  <label className="switch">
                    <input
                      checked={widget.active ? true : false}
                      type="checkbox"
                      onClick={() =>
                        dispatch(
                          activateBadge({
                            id: widget.id,
                          })
                        )
                      }
                    />
                    <span className="slider round"></span>
                  </label>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBoxs;
