import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBox.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import { updateWidget, fetchPerson } from "../../store/features/personSlice";
// import greenSparkLogo from "../../assets/images/greenspark-logo.svg";

const WidgetBox: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  const updateblue = () => {
    dispatch(updateWidget("#2E3A8C"));
  };

  const updategreen = () => {
    dispatch(updateWidget("#3B755F"));
  };

  const updatecreme = () => {
    dispatch(updateWidget("#F2EBDB"));
  };

  const updatewhite = () => {
    dispatch(updateWidget("white"));
  };

  const updateblack = () => {
    dispatch(updateWidget("black"));
  };

  const activateBadge = () => {};

  return (
    <div className="widgetBox">
      <h1>Per Product Widgets</h1>
      <hr />
      <div className="innerBoxes">
        {persons.map((person) => {
          return (
            <div className="outerBox">
              <div
                style={{
                  background: person.selectedColor,
                }}
                className="top"
              >
                <p>
                  This product collects
                  <br />
                  {person.amount} {person.type}
                </p>
              </div>
              <div className="bottom">
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <p>Link to public profile</p>
                  <input type="checkbox" />
                </div>
                <div>
                  <p>Badge Color</p>
                  <button
                    style={{
                      backgroundColor: "#2E3A8C",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updateblue}
                  ></button>
                  <button
                    style={{
                      backgroundColor: "#3B755F",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updategreen}
                  ></button>
                  <button
                    style={{
                      backgroundColor: "#F2EBDB",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updatecreme}
                  ></button>
                  <button
                    style={{
                      backgroundColor: "white",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updatewhite}
                  ></button>
                  <button
                    style={{
                      backgroundColor: "black",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updateblack}
                  ></button>
                </div>
                <div>
                  <p>Activate Badge</p>
                  <input onClick={activateBadge} type="checkbox" />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBox;
