import React from "react";
import "./WidgetInput.scss";
import { useAppDispatch } from "../../../store/store";
import { activateLinked } from "../../../store/features/widgetSlice";

type InputType = {
  id: number;
  active: boolean;
  linked: boolean;
};

interface InputProps {
  widgetData: InputType;
}

const WidgetInput: React.FC<InputProps> = ({ widgetData }) => {
  const dispatch = useAppDispatch();
  return (
    <div
      className={widgetData.active ? "profileCheckbox" : "noProfileCheckbox"}
    >
      <input
        onClick={() =>
          dispatch(
            activateLinked({
              id: widgetData.id,
            })
          )
        }
        disabled={widgetData.active ? false : true}
        checked={widgetData.linked && widgetData.active ? true : false}
        type="checkbox"
      />
    </div>
  );
};

export default WidgetInput;
