import React from "react";

type BtnType = {
  id: number;
  active: boolean;
};

interface BtnProps {
  widgetData: BtnType;
  updateColor: (id: number, boxColor: string) => void;
  boxColor: string;
}

const WidgetBtn: React.FC<BtnProps> = ({
  widgetData,
  boxColor,
  updateColor,
}) => {
  return (
    <div>
      <button
        style={{
          backgroundColor: boxColor,
          cursor: "pointer",
        }}
        onClick={() => updateColor(widgetData.id, boxColor)}
      ></button>
    </div>
  );
};

export default WidgetBtn;
