export const WidgetSlice = createSlice({
  name: "widget",
  initialState,
  reducers: {
    updateWidget: (
      state,
      action: PayloadAction<{ id: number; selectedColor: string }>
    ) => {
      const index = action.payload.id;
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          index === content.id
            ? { ...content, selectedColor: action.payload.selectedColor }
            : content
        ),
      };
    },
    activateLinked: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, linked: !content.linked }
            : action.payload.id !== content.id
            ? { ...content, linked: false }
            : content
        ),
      };
    },
    activateBadge: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, active: !content.active }
            : action.payload.id !== content.id
            ? { ...content, active: false }
            : content
        ),
      };
    },
    updateColorOnMount: (
      state,
      action: PayloadAction<{ id: number; selectedColor: string }>
    ) => {
      const index = action.payload.id;
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          index === content.id
            ? { ...content, selectedColor: action.payload.selectedColor }
            : content
        ),
      };
      // return {
      //   ...state,
      // widgetData: state.widgetData.map((content) =>
      //   content.selectedColor === "green"
      //     ? { ...content, selectedColor: "#2E3A8C" }
      //     : content.selectedColor === "black"
      //     ? {
      //         ...content,
      //         selectedColor: "orange",
      //       }
      //     : content.selectedColor === "blue"
      //     ? {
      //         ...content,
      //         selectedColor: "pink",
      //       }
      //     : content
      // ),
      // };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(
      fetchWidgetData.fulfilled,
      (state, action: PayloadAction<WidgetData[]>) => {
        state.widgetData = action.payload;
      }
    );
    // builder.addCase(fetchWidgetData.rejected, (state, action) => {
    //   if (action.payload) {
    //     state.error = action.payload.errorMessage
    //   } else {
    //     state.error = action.error.message
    //   }
  },
});

export default WidgetSlice.reducer;
export const {
  updateWidget,
  activateBadge,
  activateLinked,
  updateColorOnMount,
} = WidgetSlice.actions;
