import React from "react";
import { useAppDispatch, useAppSelector } from "../../store/store";

type UserType = {
  id: number;
  active: boolean;
  boxColor: string;
};

interface UserProps {
  widgetData: UserType;
  updateColor: (id: number) => void;
}

const WidgetBtn: React.FC<UserProps> = ({
  widgetData,
  boxColor,
  updateColor,
}) => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);
  return (
    <div>
      <button
        disabled={widgetData.active ? false : true}
        style={{
          backgroundColor: boxColor,
        }}
        onClick={() => updateColor(widgetData.id)}
      ></button>
    </div>
  );
};

export default WidgetBtn;
