import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";

export interface Person {
  id: number;
  action: string;
  active: boolean;
  amount: number;
  linked: boolean;
  selectedColor: string;
  type: string;
}

// export interface Colors {
//   selectedColor: string;
// }

interface PersonState {
  persons: Person[];
  //   colors: Colors[];
}

const initialState: PersonState = {
  persons: [],
  //   colors: [],
};

// const initialState: PersonState = {
//   persons: [],
// };

export const fetchPerson = createAsyncThunk(
  "person/fetch",
  async (thunkAPI) => {
    const response = await fetch(
      "https://api.mocki.io/v2/016d11e8/product-widgets",
      {
        method: "GET",
      }
    );
    const data = response.json();
    console.log(response, "RESPONSE HERE YOOO");
    return data;
  }
);

export const PersonSlice = createSlice({
  name: "person",
  initialState,
  reducers: {
    // addPerson: (state, action) => {
    //   state.persons = action.payload;
    // },

    addPerson: (state, action: PayloadAction<any>) => {
      const index = state.persons.findIndex((i) => i.id === action.payload.id);
      return {
        ...state,
        persons: [...state.persons, action.payload[index]],
      };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchPerson.fulfilled, (state, action) => {
      state.persons = action.payload;
    });
    // builder.addCase(savePerson.fulfilled, (state, action) => {
    //   state.persons.push(action.payload);
    // });
  },
});

export default PersonSlice.reducer;
export const { addPerson } = PersonSlice.actions;
