import React, { useEffect, useRef, useState } from "react";
import "./WidgetBoxes.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import {
  updateWidget,
  fetchWidgetData,
  activateBadge,
  activateLinked,
  updateWidgetColor,
} from "../../store/features/widgetSlice";
import greenSparkLogo from "../../assets/images/greenspark-logo.svg";
import greenSparkLogoGreen from "../../assets/images/greenspark-logo-green.svg";
import greenSparkLogoBlack from "../../assets/images/greenspark-logo-black.svg";
import toolTipImg from "../../assets/images/Tooltip.svg";
import WidgetBtn from "../UI/WidgetBtn/WidgetBtn";
import WidgetInput from "../UI/WidgetInput/WidgetInput";
import SkeletonLoad from "../UI/SkeletonLoading/SkeletonLoad/SkeletonLoad";

const COLORS = {
  blue: "#2E3A8C",
  green: "#3B755F",
  creme: "#F2EBDB",
};

const COLORSMAP = [
  { id: 1, blue: "#2E3A8C" },
  { id: 2, green: "#3B755F" },
  { id: 3, creme: "#F2EBDB" },
];

const WidgetBoxes: React.FC = () => {
  const dispatch = useAppDispatch();
  const widgetData = useAppSelector((state) => state.widgetData.widgetData);

  const [focusedBtn, setFocusedBtn] = useState(false);
  const [focusedBtn2, setFocusedBtn2] = useState(false);
  const [focusedBtn3, setFocusedBtn3] = useState(false);
  const [focusedBtn4, setFocusedBtn4] = useState(false);
  const [focusedBtn5, setFocusedBtn5] = useState(false);

  const setFocusOne = () => {
    setFocusedBtn(!focusedBtn);
    setFocusedBtn2(false);
  };

  const setFocusTwo = () => {
    setFocusedBtn2(!focusedBtn2);
    setFocusedBtn(false);
  };

  useEffect(() => {
    dispatch(fetchWidgetData())
      .unwrap()
      .then(() => dispatch(updateWidgetColor()))
      .catch((e) => e);
  }, []);

  const updateColor = (index: number, boxColor: string, colorID: number) => {
    setFocusedBtn(true);
    dispatch(
      updateWidget({ id: index, selectedColor: boxColor, boxID: colorID })
    );
  };

  const updateInputLinked = (index: number) => {
    dispatch(activateLinked({ id: index }));
  };

  const updateInputBadge = (index: number) => {
    dispatch(activateBadge({ id: index }));
  };

  return (
    <div className="widgetBox">
      <h3>Per product widgets</h3>
      <hr />
      {widgetData.length > 0 ? (
        <div className={"innerBoxes"}>
          {widgetData.map((widget, i) => {
            return (
              <div key={i} className="outerBox">
                <div
                  style={{
                    background: widget.selectedColor,
                    color:
                      widget.selectedColor === COLORS.creme
                        ? COLORS.green
                        : widget.selectedColor === "white"
                        ? "black"
                        : "white",
                  }}
                  className="top"
                >
                  <img
                    src={
                      widget.selectedColor === COLORS.creme
                        ? greenSparkLogoGreen
                        : widget.selectedColor === "white"
                        ? greenSparkLogoBlack
                        : greenSparkLogo
                    }
                    alt="greensparkLogo"
                  />
                  <div className="widgetContainer">
                    <p>This product {widget.action}</p>
                    <br />
                    <div className="widgetTitle">
                      {widget.type === "carbon" ? (
                        <>
                          <span>
                            {widget.amount}kgs of {widget.type}
                          </span>
                        </>
                      ) : (
                        <>
                          <span>
                            {widget.amount} {widget.type}
                          </span>
                        </>
                      )}
                    </div>
                  </div>
                </div>
                <div className="bottom">
                  {/* <h1>{JSON.stringify(boxColors)}</h1> */}
                  <div className="innerBottom">
                    <div className="publicProfile">
                      <p>Link to Public Profile</p>
                      <div className="tooltip">
                        <img src={toolTipImg} alt="toolTipImg" />
                        <span className="tooltiptext">
                          This widget links directly to your public profile so
                          that you can easily share your impact with your
                          customers. Turn it off here if you do not want the
                          badge to link to it.
                          <a
                            aria-disabled
                            href="https://brett-dickenson-portfolio.netlify.app/"
                          >
                            View Public Profile
                          </a>
                        </span>
                      </div>
                    </div>
                    <WidgetInput
                      toggleStyles={false}
                      checkedState={widget.linked ? true : false}
                      updateInput={updateInputLinked}
                      widgetData={widget}
                    />
                  </div>
                  <div className="coloredBtns">
                    <p>Badge color</p>
                    <div className="colorBox">
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.blue}
                        focusedBtn={focusedBtn}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.green}
                        focusedBtn={focusedBtn2}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.creme}
                        focusedBtn={focusedBtn3}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={"white"}
                        focusedBtn={focusedBtn4}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={"black"}
                        focusedBtn={focusedBtn5}
                      />
                    </div>
                  </div>
                  <div className="activateBadge">
                    <button
                      style={
                        focusedBtn
                          ? { border: "1px solid red" }
                          : { border: "transparent" }
                      }
                      onClick={setFocusOne}
                    >
                      blue BOX
                    </button>
                    <h1>{JSON.stringify(focusedBtn)}</h1>
                    <button
                      style={
                        focusedBtn2
                          ? { border: "1px solid red" }
                          : { border: "transparent" }
                      }
                      onClick={setFocusTwo}
                    >
                      GREEN BOX
                    </button>
                    <h1>{JSON.stringify(focusedBtn2)}</h1>
                    <p>Activate badge</p>
                    <WidgetInput
                      toggleStyles={true}
                      checkedState={widget.active ? true : false}
                      updateInput={updateInputBadge}
                      widgetData={widget}
                    />
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="skeletonContainer">
          {[1, 2, 3].map((n) => (
            <SkeletonLoad key={n} />
          ))}
        </div>
      )}
    </div>
  );
};

export default WidgetBoxes;
