import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";

export interface Person {
  id: number;
  action: string;
  active: boolean;
  amount: number;
  linked: boolean;
  selectedColor: string;
  type: string;
}

interface PersonState {
  persons: Person[];
}

const initialState: PersonState = {
  persons: [],
};

export const fetchPerson = createAsyncThunk(
  "person/fetch",
  async (thunkAPI) => {
    const response = await fetch(
      "https://api.mocki.io/v2/016d11e8/product-widgets",
      {
        method: "GET",
      }
    );
    const data = response.json();
    return data;
  }
);

export const PersonSlice = createSlice({
  name: "person",
  initialState,
  reducers: {
    updateWidget: (
      state,
      action: PayloadAction<{ id: number; selectedColor: string }>
    ) => {
      const index = action.payload.id - 1;
      return {
        ...state,
        persons: state.persons.map((content, i) =>
          i === index
            ? { ...content, selectedColor: action.payload.selectedColor }
            : content
        ),
      };
    },

    activateBadge: (
      state,
      action: PayloadAction<{ id: number; active: boolean }>
    ) => {
      const index = action.payload.id - 1;
      return {
        ...state,
        persons: state.persons.map((content, i) =>
          index === content.id
            ? { ...content, active: action.payload.active }
            : content
        ),
      };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(
      fetchPerson.fulfilled,
      (state, action: PayloadAction<Person[]>) => {
        state.persons = action.payload;
      }
    );
    // builder.addCase(savePerson.fulfilled, (state, action) => {
    //   state.persons.push(action.payload);
    // });
  },
});

export default PersonSlice.reducer;
export const { updateWidget, activateBadge } = PersonSlice.actions;
