import React, { useEffect } from "react";
import { BtnProps } from "../../../types/globalTypes";

const WidgetBtn: React.FC<BtnProps> = ({
  widgetData,
  boxColor,
  updateColor,
  boxID,
}) => {
  console.log(widgetData.boxColors, "!!!!");
  const widgetBoxColors = widgetData?.boxColors;

  const trueColor = widgetBoxColors?.filter((trueVal) => trueVal.setFocus);

  console.log(trueColor);

  useEffect(() => {
    widgetBoxColors?.map((res) => {
      console.log(res, "MAP RES");
    });
  }, []);

  return (
    <div>
      <button
        style={
          trueColor
            ? {
                border: "1px solid red",
                backgroundColor: boxColor,
                cursor: "pointer",
              }
            : {
                backgroundColor: boxColor,
                cursor: "pointer",
              }
        }
        // style={{
        //   backgroundColor: boxColor,
        //   cursor: "pointer",
        // }}
        onClick={() => updateColor(widgetData.id, boxColor, boxID)}
      ></button>
    </div>
  );
};

export default WidgetBtn;
