import React from "react";
import "./WidgetInput.scss";
import { useAppDispatch } from "../../../store/store";
import {
  activateBadge,
  activateLinked,
} from "../../../store/features/widgetSlice";

type InputType = {
  id: number;
  active: boolean;
  linked: boolean;
};

interface InputProps {
  widgetData: InputType;
  updateInput: (id: number) => void;
  checkedState: boolean;
  toggleStyles: boolean;
}

const WidgetInput: React.FC<InputProps> = ({
  widgetData,
  updateInput,
  checkedState,
  toggleStyles,
  // activeState,
}) => {
  const dispatch = useAppDispatch();

  // const switchState = !widgetData.active || !widgetData.linked;
  return (
    <div>
      {toggleStyles ? (
        <label className="switch">
          <input
            onClick={() => updateInput(widgetData.id)}
            checked={checkedState}
            type="checkbox"
            id="c1"
            name="cc"
          />
          <span className="slider round"></span>
        </label>
      ) : (
        <div className="profileCheckbox">
          <input
            onClick={() => updateInput(widgetData.id)}
            checked={checkedState}
            type="checkbox"
            id="c1"
            name="cc"
          />
          <label></label>
        </div>
      )}
    </div>
  );
};

export default WidgetInput;
