import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { WidgetData, WidgetState } from "../../types/globalTypes";
import axios from "axios";

const initialState: WidgetState = {
  widgetData: [],
  // boxColors: [
  //   {
  //     colorID: 1,
  //     color: "#2E3A8C",
  //     setFocus: false,
  //   },
  //   {
  //     colorID: 2,
  //     color: "#3B755F",
  //     setFocus: false,
  //   },
  //   {
  //     colorID: 3,
  //     color: "#F2EBDB",
  //     setFocus: false,
  //   },
  //   {
  //     colorID: 4,
  //     color: "white",
  //     setFocus: false,
  //   },
  //   {
  //     colorID: 5,
  //     color: "black",
  //     setFocus: false,
  //   },
  // ],
};

export const fetchWidgetData = createAsyncThunk(
  "widgetData/fetch",
  async () => {
    try {
      const url = "https://api.mocki.io/v2/016d11e8/product-widgets";
      const response = await axios.get(url);
      return response.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  }
);

export const WidgetSlice = createSlice({
  name: "widget",
  initialState,
  reducers: {
    updateWidget: (
      state,
      action: PayloadAction<{
        id: number;
        selectedColor: string;
        colorID: number;
      }>
    ) => {
      const index = action.payload.id;
      const colorIndex = action.payload.colorID;
      console.log(colorIndex, "colorIndex");
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          index === content.id
            ? {
                ...content,
                selectedColor: action.payload.selectedColor,
                boxColors: state.boxColors.map((item) =>
                  colorIndex === item.colorID
                    ? { ...item, setFocus: true }
                    : { ...item, setFocus: false }
                ),
              }
            : index !== content.id
            ? {
                ...content,
                active: false,
                linked: false,
                setFocus: false,
                boxColors: [],
              }
            : content
        ),
      };
    },
    updateWidgetColor: (state) => {
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          content.selectedColor === "green"
            ? {
                ...content,
                selectedColor: "#2E3A8C",
                active: false,
                linked: true,
              }
            : content.selectedColor === "black"
            ? {
                ...content,
                selectedColor: "#3B755F",
                active: false,
                linked: true,
              }
            : content.selectedColor === "blue"
            ? {
                ...content,
                selectedColor: "#F2EBDB",
                active: false,
                linked: true,
                amount: 100,
              }
            : content
        ),
      };
    },
    activateLinked: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, linked: !content.linked }
            : action.payload.id !== content.id
            ? { ...content, linked: false, active: false }
            : content
        ),
      };
    },
    activateBadge: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, active: !content.active }
            : action.payload.id !== content.id
            ? { ...content, active: false, linked: false }
            : content
        ),
      };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(
      fetchWidgetData.fulfilled,
      (state, action: PayloadAction<WidgetData[]>) => {
        state.widgetData = action.payload;
      }
    );
  },
});

export default WidgetSlice.reducer;
export const {
  updateWidget,
  activateBadge,
  activateLinked,
  updateWidgetColor,
} = WidgetSlice.actions;
