import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBox.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import { addPerson, fetchPerson } from "../../store/features/personSlice";
// import greenSparkLogo from "../../assets/images/greenspark-logo.svg";

const WidgetBox: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  // const [color, setColor] = useState<string>("#2E3A8C");
  // const [color2, setColor2] = useState<string>("#3B755F");
  // const [color3, setColor3] = useState<string>("#F2EBDB");

  const updateblue = () => {
    dispatch(addPerson({ name: "red" }));
  };

  // const updategreen = () => {
  //   setColor("#3B755F");
  // };

  // const updatecreme = () => {
  //   setColor('#F2EBDB"');
  // };

  return (
    <div className="widgetBox">
      <h1>Per Product Widgets</h1>
      <hr />
      <div className="innerBoxes">
        {persons.map((person) => {
          return (
            <div className="outerBox">
              <div
                // style={{
                //   background:
                //     person?.id === 1
                //       ? color
                //       : person?.id === 2
                //       ? color2
                //       : color3,
                // }}
                className="top"
              >
                {/* <img className="bgImage2" src={greenSparkLogo} alt="bg-image" /> */}
                <p>
                  This product collects
                  <br />
                  {person.amount} {person.type}
                </p>
              </div>
              <div className="bottom">
                <div>
                  <p>Link to public profile</p>
                  <input type="checkbox" />
                </div>
                <div>
                  <p>Badge Color</p>
                  <button
                    style={{
                      backgroundColor: "#2E3A8C",
                      height: 24,
                      width: 24,
                    }}
                    onClick={updateblue}
                  ></button>
                  <button
                    // onClick={updategreen}
                    style={{
                      backgroundColor: "#3B755F",
                      height: 24,
                      width: 24,
                    }}
                  ></button>
                  <button
                    // onClick={updatecreme}
                    style={{
                      backgroundColor: "#F2EBDB",
                      height: 24,
                      width: 24,
                    }}
                  ></button>
                </div>
                <div>
                  <p>Activate Box</p>
                  <input type="checkbox" />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBox;
