    //       )
    //     }
    //     // disabled={widgetData.active ? false : true}
    //     checked={widgetData.linked ? true : false}
    //     type="checkbox"
    //   />
    // </div>
    <form action="">
      <label className="form-control">
        <input
          onClick={() =>
            dispatch(
              activateLinked({
                id: widgetData.id,
              })
            )
          }
          type="checkbox"
          name="checkbox"
          checked={widgetData.linked ? true : false}
        />
        {/* Checkbox */}
      </label>
    </form>
  );
};

export default WidgetInput;
