import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { Person, PersonState } from "../../types/globalTypes";

const initialState: PersonState = {
  widgetData: [],
};

export const fetchWidgetData = createAsyncThunk(
  "widgetData/fetch",
  async (thunkAPI) => {
    const response = await fetch(
      "https://api.mocki.io/v2/016d11e8/product-widgets",
      {
        method: "GET",
      }
    );
    const data = response.json();
    return data;
  }
);

export const WidgetSlice = createSlice({
  name: "widget",
  initialState,
  reducers: {
    updateWidget: (
      state,
      action: PayloadAction<{ id: number; selectedColor: string }>
    ) => {
      const index = action.payload.id;
      return {
        ...state,
        widgetData: state.widgetData.map((content, i) =>
          index === content.id
            ? { ...content, selectedColor: action.payload.selectedColor }
            : content
        ),
      };
    },
    activateLinked: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        persons: state.persons.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, linked: !content.linked }
            : action.payload.id !== content.id
            ? { ...content, linked: false }
            : content
        ),
      };
    },
    activateBadge: (state, action: PayloadAction<{ id: number }>) => {
      return {
        ...state,
        persons: state.persons.map((content, i) =>
          action.payload.id === content.id
            ? { ...content, active: !content.active }
            : action.payload.id !== content.id
            ? { ...content, active: false }
            : content
        ),
      };
    },
  },
  extraReducers: (builder) => {
    builder.addCase(
      fetchWidgetData.fulfilled,
      (state, action: PayloadAction<Person[]>) => {
        state.widgetData = action.payload;
      }
    );
    // builder.addCase(savePerson.fulfilled, (state, action) => {
    //   state.persons.push(action.payload);
    // });
  },
});

export default WidgetSlice.reducer;
export const { updateWidget, activateBadge, activateLinked } =
  WidgetSlice.actions;
