import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import "./WidgetBoxs.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import {
  updateWidget,
  fetchPerson,
  activateBadge,
  activateLinked,
} from "../../store/features/personSlice";
import greenSparkLogo from "../../assets/images/greenspark-logo.svg";
import greenSparkLogoGreen from "../../assets/images/greenspark-logo-green.svg";
import greenSparkLogoBlack from "../../assets/images/greenspark-logo-black.svg";
import toolTipImg from "../../assets/images/Tooltip.svg";

const WidgetBoxs: React.FC = () => {
  const dispatch = useAppDispatch();
  const persons = useAppSelector((state) => state.person.persons);

  useEffect(() => {
    dispatch(fetchPerson());
  }, []);

  const updateblue = (index: number) => {
    dispatch(updateWidget({ id: index, selectedColor: "#2E3A8C" }));
  };

  const updategreen = (index: number) => {
    dispatch(updateWidget({ id: index, selectedColor: "#3B755F" }));
  };

  const updatecreme = (index: number) => {
    dispatch(updateWidget({ id: index, selectedColor: "#F2EBDB" }));
  };

  const updatewhite = (index: number) => {
    dispatch(updateWidget({ id: index, selectedColor: "white" }));
  };

  const updateblack = (index: number) => {
    dispatch(updateWidget({ id: index, selectedColor: "black" }));
  };

  return (
    <div className="widgetBox">
      <h3>Per Product Widgets</h3>
      <hr />
      <div className="innerBoxes">
        {persons.map((person, i) => {
          return (
            <div key={i} className="outerBox">
              <div
                style={{
                  background: person.selectedColor,
                  color:
                    person.selectedColor === "#F2EBDB"
                      ? "#3B755F"
                      : person.selectedColor === "white"
                      ? "black"
                      : "white",
                }}
                className="top"
              >
                <img
                  src={
                    person.selectedColor === "#F2EBDB"
                      ? greenSparkLogoGreen
                      : person.selectedColor === "white"
                      ? greenSparkLogoBlack
                      : greenSparkLogo
                  }
                  alt="greensparkLogo"
                />
                <p>
                  This product {person.action}
                  <br />
                  <span>
                    {person.amount}
                    {person.type === "carbon" ? <p> kgs of </p> : null}{" "}
                    {person.type}
                  </span>
                </p>
              </div>
              <div className="bottom">
                <div className="innerBottom">
                  <div className="publicProfile">
                    <p>Link to Public Profile</p>
                    <div className="tooltip">
                      <img src={toolTipImg} alt="toolTipImg" />
                      <span className="tooltiptext">
                        This widget links directly to your public profile so
                        that you can easily share your impact with your
                        customers. Turn it off here if you do not want the badge
                        to link to it.
                        <a href="https://brett-dickenson-portfolio.netlify.app/">
                          View Public Profile
                        </a>
                      </span>
                    </div>
                  </div>
                  <div className={person.active ? "navi-dots" : "navi-dots-no"}>
                    <input
                      onClick={() =>
                        dispatch(
                          activateLinked({
                            id: person.id,
                          })
                        )
                      }
                      checked={person.linked && person.active ? true : false}
                      type="checkbox"
                    />
                  </div>
                </div>
                <div className="coloredBtns">
                  <p>Badge color</p>
                  <div style={{ maxWidth: "100px", display: "flex" }}>
                    <div className="colorBox">
                      <button
                        disabled={person.active ? false : true}
                        style={{
                          backgroundColor: "#2E3A8C",
                        }}
                        onClick={() => updateblue(person.id)}
                      ></button>
                    </div>

                    <div className="colorBox">
                      <button
                        disabled={person.active ? false : true}
                        style={{
                          backgroundColor: "#3B755F",
                        }}
                        onClick={() => updategreen(person.id)}
                      ></button>
                    </div>
                    <div className="colorBox">
                      <button
                        disabled={person.active ? false : true}
                        style={{
                          backgroundColor: "#F2EBDB",
                        }}
                        onClick={() => updatecreme(person.id)}
                      ></button>
                    </div>
                    <div className="colorBox">
                      <button
                        disabled={person.active ? false : true}
                        style={{
                          backgroundColor: "white",
                        }}
                        onClick={() => updatewhite(person.id)}
                      ></button>
                    </div>
                    <div className="colorBox">
                      <button
                        disabled={person.active ? false : true}
                        style={{
                          backgroundColor: "black",
                        }}
                        onClick={() => updateblack(person.id)}
                      ></button>
                    </div>
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <p>Activate badge</p>
                  {/* <p>{JSON.stringify(person.active)}</p>= */}
                  <label className="switch">
                    <input
                      checked={person.active ? true : false}
                      type="checkbox"
                      onClick={() =>
                        dispatch(
                          activateBadge({
                            id: person.id,
                          })
                        )
                      }
                    />

                    <span className="slider round"></span>
                  </label>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WidgetBoxs;
