import React, { useEffect } from "react";
import "./WidgetBoxs.scss";
import { useAppDispatch, useAppSelector } from "../../store/store";
import {
  updateWidget,
  fetchWidgetData,
  activateBadge,
} from "../../store/features/widgetSlice";
import greenSparkLogo from "../../assets/images/greenspark-logo.svg";
import greenSparkLogoGreen from "../../assets/images/greenspark-logo-green.svg";
import greenSparkLogoBlack from "../../assets/images/greenspark-logo-black.svg";
import toolTipImg from "../../assets/images/Tooltip.svg";
import WidgetBtn from "../UI/WidgetBtn/WidgetBtn";
import WidgetInput from "../UI/WidgetInput/WidgetInput";

import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";

const COLORS = {
  blue: "#2E3A8C",
  green: "#3B755F",
  creme: "#F2EBDB",
};

const WidgetBoxs: React.FC = () => {
  const dispatch = useAppDispatch();
  const widgetData = useAppSelector((state) => state.widgetData.widgetData);

  useEffect(() => {
    dispatch(fetchWidgetData());
  }, []);

  const updateColor = (index: number, boxColor: string) => {
    dispatch(updateWidget({ id: index, selectedColor: boxColor }));
  };

  return (
    <div className="widgetBox">
      <h3>Per product widgets</h3>
      <hr />
      {widgetData.length > 0 ? (
        <div className="innerBoxes">
          {widgetData.map((widget, i) => {
            return (
              <div key={i} className="outerBox">
                <div
                  style={{
                    background: widget.selectedColor,
                    color:
                      widget.selectedColor === COLORS.creme
                        ? COLORS.green
                        : widget.selectedColor === "white"
                        ? "black"
                        : "white",
                  }}
                  className="top"
                >
                  <img
                    src={
                      widget.selectedColor === COLORS.creme
                        ? greenSparkLogoGreen
                        : widget.selectedColor === "white"
                        ? greenSparkLogoBlack
                        : greenSparkLogo
                    }
                    alt="greensparkLogo"
                  />
                  <div className="widgetContainer">
                    <p>This product {widget.action}</p>
                    <br />
                    <div className="widgetTitle">
                      {widget.type === "carbon" ? (
                        <>
                          <span>
                            {widget.amount} kgs of {widget.type}
                          </span>
                        </>
                      ) : (
                        <>
                          <span>
                            {widget.amount} {widget.type}
                          </span>
                        </>
                      )}
                    </div>
                  </div>
                </div>
                <div className="bottom">
                  <div className="innerBottom">
                    <div className="publicProfile">
                      <p>Link to Public Profile</p>
                      <div className="tooltip">
                        <img src={toolTipImg} alt="toolTipImg" />
                        <span className="tooltiptext">
                          This widget links directly to your public profile so
                          that you can easily share your impact with your
                          customers. Turn it off here if you do not want the
                          badge to link to it.
                          <a
                            aria-disabled
                            href="https://brett-dickenson-portfolio.netlify.app/"
                          >
                            View Public Profile
                          </a>
                        </span>
                      </div>
                    </div>
                    <WidgetInput widgetData={widget} />
                  </div>
                  <div className="coloredBtns">
                    <p>Badge color</p>
                    <div className="colorBox">
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.blue}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.green}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={COLORS.creme}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={"white"}
                      />
                      <WidgetBtn
                        updateColor={updateColor}
                        widgetData={widget}
                        boxColor={"black"}
                      />
                    </div>
                  </div>
                  <div className="activateBadge">
                    <p>Activate badge</p>
                    <label className="switch">
                      <input
                        checked={widget.active ? true : false}
                        type="checkbox"
                        onClick={() =>
                          dispatch(
                            activateBadge({
                              id: widget.id,
                            })
                          )
                        }
                      />
                      <span className="slider round"></span>
                      {/* <p>{JSON.stringify(widget.active)}</p> */}
                    </label>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <Skeleton />
      )}
    </div>
  );
};

export default WidgetBoxs;
